import { useEffect, useRef, useState } from "react";
import { Col, Row } from "react-bootstrap";
import { connect, RootStateOrAny } from "react-redux";
import { actions } from "../../actions and const/actions";
import "./chartFormula.css";

let useClickOutside = (handler: any) => {
    let domNode = useRef<HTMLDivElement>(null);
    useEffect(() => {
        let maybeHandler = (e: MouseEvent) => {
            if (!domNode!.current!.contains(e.target as Node)) {
                handler();
            }
        };
        document.addEventListener("mousedown", maybeHandler);
        return () => {
            document.removeEventListener("mousedown", maybeHandler);
        };
    });
    return domNode;
};

type PropsTypeInputsChart = {
    title: string;
    callback: (n: string) => void;
    value: string;
    onClick?: () => void;
};

type PropsType = {
    setCounterYear: (year: string) => void;
    countYear: string;
};

const InputsChart = (props: PropsTypeInputsChart) => {
    const takeNumber = (v: string, value: string) => {
        if (isNaN(+v) != true) {
            if ((v + value).toString().length <= 3) {
                return v;
            } else {
                return value;
            }
        } else {
            return value;
        }
    };

    const { title, callback, value, onClick } = props;

    return (
        <div className="">
            <p className="inputsChart1" style={{ fontSize: "26px" }} onClick={onClick}>
                <div className="inputContainer">
                    <p className="inputContainer-inputTitle">{title}</p>
                    <span className="required"></span>
                    <br />
                    <input
                        type="text"
                        value={value}
                        className="inputRowChart"
                        onChange={(e) => {
                            // callback(e.target.value);
                            callback(takeNumber(e.target.value, value));
                        }}
                    />
                </div>
            </p>
        </div>
    );
};

const ChartFormula = (props: PropsType) => {
    const [initialAmount, setInitialAmount] = useState<string>("");

    const [isReplenishmentDropmenuOpen, setIsReplenishmentDropmenuOpen] = useState<boolean>(false);

    const domNodePeriodicity = useClickOutside(() => {
        setIsReplenishmentDropmenuOpen(false);
    });

    return (
        <>
            <div className="body-inputsContainer" style={{ marginTop: "50px" }}>
                <Row className="inputRowContainer">
                    <Col md={{ offset: 1, span: 7 }}>
                        <p className="inputsChart1" style={{ fontSize: "26px" }}>
                            <div className="inputContainer">
                                <p className="inputContainer-inputTitle">initial amount</p>
                                <span className="required"></span>
                                <br />
                                <input type="text" name="your-name" value="" className="inputRowChart" />{" "}
                            </div>
                        </p>
                    </Col>
                </Row>
                <Row className="inputRowContainer">
                    <Col md={{ offset: 1, span: 5 }}>
                        <InputsChart title={"replenishment"} callback={setInitialAmount} value={"3"} />
                    </Col>
                    <Col md={{ offset: 0, span: 5 }}>
                        <div className="">
                            <p className="inputsChart1" style={{ fontSize: "26px" }} onClick={onClick}>
                                <div className="inputContainer">
                                    <p className="inputContainer-inputTitle">Periodicity</p>
                                    <span className="required"></span>
                                    <br />
                                    <input
                                        type="text"
                                        // value={value}
                                        className="inputRowChart"
                                        onChange={(e) => {
                                            setInitialAmount(takeNumber(e.target.value, value));
                                        }}
                                    />
                                </div>
                            </p>
                        </div>

                        {/*               
                        <InputsChart
                            title={"Periodicity"}
                            callback={setInitialAmount}
                            value={"3"}
                            ref={domNodePeriodicity}
                            onClick={() => {
                                setIsReplenishmentDropmenuOpen(!isReplenishmentDropmenuOpen);
                            }}
                        /> */}

                        {isReplenishmentDropmenuOpen ? (
                            <>
                                <div className="replenishmentDropmenuOpen-dropItem">EVERY YEAR</div>
                                <div className="replenishmentDropmenuOpen-dropItem">EVERY MONTH</div>
                            </>
                        ) : (
                            <div className=""></div>
                        )}
                    </Col>
                </Row>
                <Row className="inputRowContainer">
                    <Col md={{ offset: 1, span: 5 }}>
                        <InputsChart title={"Interest accruals"} callback={setInitialAmount} value={"3"} />
                    </Col>
                    <Col md={{ offset: 0, span: 5 }}>
                        <p className="inputsChart" style={{ fontSize: "48px", textAlign: "center", left: "40%" }}>
                            YEAR
                        </p>
                    </Col>
                </Row>
                <Row className="inputRowContainer">
                    <Col md={{ offset: 1, span: 10 }}>
                        <InputsChart
                            title={"Number of years"}
                            callback={props.setCounterYear}
                            value={props.countYear}
                        />
                    </Col>
                </Row>
            </div>
        </>
    );
};

const mapStateToProps = (state: RootStateOrAny) => ({
    countYear: state.generalState.countYear,
});

export default connect(mapStateToProps, { setCounterYear: actions.setCounterYear })(ChartFormula);
