import { useState } from "react";
import { Row, Col, DropdownButton, Dropdown } from "react-bootstrap";
import { connect } from "react-redux";
import "./chart.css";

type PropsTypeInputsChart = {
    title: string;
    callback: (n: number) => void;
    value: number;
    onClick?: () => void;
};

const InputsChart = (props: PropsTypeInputsChart) => {
    const { title, callback, value, onClick } = props;

    return (
        <p className="inputsChart1" style={{ fontSize: "26px" }} onClick={onClick}>
            <div className="inputContainer">
                <p className="inputContainer-inputTitle">{title}</p>
                <span className="required"></span>
                <br />
                <input type="text" name="your-name" value="" className="inputRowChart" />{" "}
            </div>
        </p>

        // <p className="inputsChart" style={{ fontSize: "26px" }}>
        //     {title} <span className="required"></span>
        //     <br />
        //     <input type="text" name="your-name" value="" className="inputRowChart" />{" "}
        // </p>
    );
};

const Chart = () => {
    const [initialAmount, setInitialAmount] = useState<number>(0);

    const [isReplenishmentDropmenuOpen, setIsReplenishmentDropmenuOpen] = useState<boolean>(false);

    return (
        <>
            <div className="body">
                <Row>
                    <div className="body-container">
                        <div style={{ height: "100%", width: "100%", position: "relative" }}>
                            <Row style={{ height: "100%", width: "100%" }}>
                                <Col md={{ offset: 1, span: 10 }} style={{ position: "relative" }}>
                                    <div className="stickContainer">
                                        <div className="stickContainer-stick">g</div>
                                        <div className="stickContainer-stick">g</div>
                                        <div className="stickContainer-stick">g</div>
                                    </div>
                                </Col>
                            </Row>
                        </div>
                    </div>
                    <div className="body-inputsContainer">
                        <Row className="inputRowContainer">
                            <Col md={{ offset: 1, span: 7 }}>
                                <p className="inputsChart1" style={{ fontSize: "26px" }}>
                                    <div className="inputContainer">
                                        <p className="inputContainer-inputTitle">initial amount</p>
                                        <span className="required"></span>
                                        <br />
                                        <input type="text" name="your-name" value="" className="inputRowChart" />{" "}
                                    </div>
                                </p>
                            </Col>
                        </Row>
                            <Row className="inputRowContainer">
                                <Col md={{ offset: 1, span: 5 }}>
                                    <InputsChart title={"replenishment"} callback={setInitialAmount} value={3} />
                                </Col>
                                <Col md={{ offset: 0, span: 5 }}>
                                    <InputsChart
                                        title={"Periodicity"}
                                        callback={setInitialAmount}
                                        value={3}
                                        onClick={() => {
                                            setIsReplenishmentDropmenuOpen(!isReplenishmentDropmenuOpen);
                                        }}
                                    />

                                    {isReplenishmentDropmenuOpen ? (
                                        <>
                                            <div className="replenishmentDropmenuOpen-dropItem">EVERY YEAR</div>
                                            <div className="replenishmentDropmenuOpen-dropItem">EVERY MONTH</div>
                                        </>
                                    ) : (
                                        <div className=""></div>
                                    )}
                                </Col>
                            </Row>
                            <Row className="inputRowContainer">
                                <Col md={{ offset: 1, span: 5 }}>
                                    <InputsChart title={"Interest accruals"} callback={setInitialAmount} value={3} />
                                </Col>
                                <Col md={{ offset: 0, span: 5 }}>
                                    <p
                                        className="inputsChart"
                                        style={{ fontSize: "48px", textAlign: "center", left: "40%" }}
                                    >
                                        YEAR
                                    </p>
                                </Col>
                            </Row>
                            <Row className="inputRowContainer">
                                <Col md={{ offset: 1, span: 10 }}>
                                    <InputsChart title={"Number of years"} callback={setInitialAmount} value={3} />
                                </Col>
                            </Row>
                    </div>
                </Row>
            </div>
        </>
    );
};

// export default Chart

export default connect(null, null)(Chart);
